import "log"

//Parse tree node types enumeration
//TODO: figure out a way to encapsualte this in 
//a more idiomatic fashion

type nodetype int
type datumtype int

// Node types are autogenerated from gram.y in Makefile

// Parse tree "wrapper" struct
type children []Pnode

type ptree struct {
	tree	children
	query	string
}

// Parse tree node
type Pnode struct {
	tag	int
	tree	[]Pnode
	val	Expr
}

// Expression tree
type Expr struct {
	data	Datum
	left	*Expr
	right	*Expr
}

// Datum interface
// Need to add some methods here 
// but leave it blank for the time being

type Datum struct {
	value	datumval
	dtype	datumtype
}

func (p *Pnode) append_node(n Pnode) {
	p.tree = append(p.tree, n)
}

func make_identifier(i string) Pnode {
	return Pnode{
		tag: identifier,
		val: Expr{
			data: Datum{
				value: i,
				dtype: IDENTIFIER,
			},
		},
	}
}

func Walk_ptree(t ptree) {
	for _ , p := range t.tree {
	  p.walk_pnode()
	}
}

func (t Pnode) walk_pnode() {

	//traverse 'tree' slice left-depth first
	var p Pnode

	if (t.tree != nil) {
		for _ , p = range t.tree {

			p.walk_pnode()
		}

	}
	log.Printf("Node: %s \n" , exprTokname(p.tag));
}


