type Datum struct {
	value	datumval
	dtype	datumtype
}

func (p *Pnode) append_node(n Pnode) {
	p.tree = append(p.tree, n)
}

func make_identifier(i string) Datum {
	return Datum{
				value: i,
				dtype: IDENTIFIER}
}


func make_scalar_expr(d Datum, l *Expr , r *Expr) *Expr {
	return &Expr{
			data: d,
			left: l,
			right: r}
}


