// Walk parse tree for debugging purposes
func (t Pnode) walkParseTree() {

	var f = func(l Pnode)(bool,Pnode){
		log.Print("calling func\n")
		log.Printf("fn: current pnode: %s %d %+v ",typName(l.tag), l.tag, l.val)
		return false,Pnode{}
	}
	_, a := t.walk_pnode(f,0)
	log.Printf("%+v\n", a)
}
